<?php/** * do action to ger row */if(isset($_POST) && $_POST!=NULL) {	require realpath( __DIR__ ) . "/db_manager.php";	$t_name = $_POST['t_name'];	$r_ID = $_POST['ID'];	$row = get_row($t_name,$r_ID);	//var_dump($row[0]);	/**	 * create result like form:	 */	$form .= '<div class="postbox"><span class="close dashicons dashicons-no-alt" title="Close the form"></span><div class="inside"><table cellpadding="8" class="tform_update"><tbody>';	foreach($row[0] as $key => $item){		$form .='<tr ';		if($key == 'ID'){			$form .= ' readonly style="display:none;"';		}		$form .='><td>'.$key.':</td><td><textarea type="text" name="'.$key.'" id="'.$key.'" value="" ';		if($key == 'Search_rule'){			$form .=' required ';		}		$form .='>'.$item.'</textarea></td></tr>';	}	$form .= '<tr><td><input type="submit" name="send" class="button button-primary send" value="update"></td></tr></tbody></table></div></div>';	/**	 * end of creating form.	 */	$form .='<script>				jQuery(".close").click(function(){jQuery(this).parent().parent().fadeOut();})			</script>';	echo $form;}