<?php/** * do action to get table */if(isset($_POST['name']) && $_POST['name']!="") {    require realpath(__DIR__) . "/db_manager.php";    $t_name = $_POST['name'];    /**     * start create table:     */    $table .= '<table width="100%" class="wp-list-table widefat striped pages">                    <thead class="thead">                        <tr>';                            foreach (get_colums_name($t_name) as $key => $colum) {                                $table .= '<th>' . $colum;                                if($key != 'ID' && $key != 1){                                    $table .=' <span class="dashicons dashicons-dismiss delet_col" style="color:red;" title="Remove column"></span>';                                }                                $table .= '</th>';                                if($key == 'ID') {                                    $table .= '<th></th>';                                }                            }                        $table .= '</tr>                    </thead>';        $table .= '<tbody>';                foreach (get_all($t_name) as $row => $value) {                    $table .= '<tr class="data_row ';                    if($row%2 == 1){                        $table .= 'row_bg';                    }                    $table .= '">';                    foreach ($value as $key => $data) {                        $table .= '<td>' . $data . '</td>';                        if($key == 'ID'){                            $table .= '<td class="dell" style="text-align: center;vertical-align: middle;color:red;font-weight: bold;"><span class="dashicons dashicons-dismiss" title="Remove the row!"></span></td>';                        }                    }                    $table .= '</tr>';                }        $table .= '</tbody>';        $table .= '<tfoot class="tfoot">                        <tr>';                 foreach (get_colums_name($t_name) as $key => $colum) {                     $table .= '<th>' . $colum . '</th>';                     if($key == 'ID') {                         $table .= '<th></th>';                     }                 }            $table .= '</tr>                </tfoot>';                   $table .= '</table>';    /**     * end creat table     */    /**     * start add the action script     */    $table .='<script>        (function( $ ) {$(document).ready(function(){            var t_name = $("#form .table_name").val();            $(".data_row").dblclick(function(){                var ID = $(this).find(":first-child").text();                get_row(ID,t_name);            });            $(".dell").click(function(){                var ID = $(this).prev().text();                del_row(ID,t_name);            });            $(".delet_col").click(function(){                var c_name = $(this).parent().text();                del_col(t_name,c_name);            });        });})(jQuery);    </script>';    /**     * end add script     */    echo $table;}