<?php/** * Set new row action */if(isset($_POST) && $_POST!=NULL) {	require realpath( __DIR__ ) . "/db_manager.php";	$t_name="static_translate";	$value=[$_POST['Search_rule']];	$value = preg_replace_callback("/(&#[0-9]+;)/", function($m) { return mb_convert_encoding($m[1], "UTF-8", "HTML-ENTITIES"); },$value);	$value = str_replace("\\",'',$value);	add_row($t_name,$value);}